   // be mean centered but not normalized) and the SIMPLS algorithm. 
   PartialLeastSquaresAnalysis pls = new PartialLeastSquaresAnalysis(inputs, outputs,
       AnalysisMethod.Center, PartialLeastSquaresAlgorithm.SIMPLS);

